package object_p;

public class MathMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Math mm = new Math();
		System.out.println(Math.PI);
		System.out.println(Math.ceil(123.456));
		System.out.println(Math.ceil(-123.456));
		System.out.println(Math.floor(123.756));
		System.out.println(Math.floor(-123.756));
		System.out.println(Math.round(123.456));
		System.out.println(Math.round(-123.456));
		System.out.println(Math.round(123.756));
		System.out.println(Math.round(-123.756));
		System.out.println(Math.max(100, 200));
		System.out.println(Math.min(100, 200));
		System.out.println(Math.pow(2, 4));
		System.out.println(Math.sqrt(2));
		System.out.println(Math.log(28));
		System.out.println(Math.exp(1));
		System.out.println(Math.toRadians(30));
		System.out.println(Math.PI/180);
		System.out.println(Math.sin(30*Math.PI/180));
		System.out.println(Math.sin(60*Math.PI/180));
		System.out.println(Math.sin(45*Math.PI/180));
		
		System.out.println(Math.random());
		System.out.println((int)(Math.random()*50));
	}

}
